INSERT INTO `blogcomments` (`id`, `comment_posts_id`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_ip`, `comment_date`, `comment_content`, `comment_karma`, `changed`, `comment_type`, `comment_status`) VALUES (1045,1745,'Alex\n','','\n','130.226.56.2','2004-07-26 16:51:01','I have worked on it for some weeks, it still needs some tests, but I am pleased to come with a solution.<br />\nI have made a small function that handle the main HTTP/1.x conditional requests, HEAD requests, and has a special mode to give to the client only the new articles he did not received yet.<br />\nThere is not need to modify anything client or server side, since all the HTTP/1.x compliant devices handle this functionality natively.<br />\nTested with PHP4, PHP5, Apache, IIS, Mozilla, Internet Explorer, SharpReader but more tests are required.<br />\nThis should save quite a lot of resources.<br />\nPlease give it a try; your comments are welcome.<br />\nhttp://alexandre.alapetite.net/doc-alex/php-http-304/\n',0,'2004-09-26 16:57:21','NORMAL',1);
