INSERT INTO `blogcomments` (`id`, `comment_posts_id`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_ip`, `comment_date`, `comment_content`, `comment_karma`, `changed`, `comment_type`, `comment_status`) VALUES (1040,1746,'Isotopp\n','','\n','195.158.168.65','2004-07-25 21:31:45','Darn. Again, replace {} with angular brackets.<br />\n<br />\nkris@valiant:~/Source/phpfaq} php sample.php<br />\nSimpleXMLElement Object<br />\n(<br />\n[y] =} SimpleXMLElement Object<br />\n(<br />\n[y] =} SimpleXMLElement Object<br />\n(<br />\n[c] =} Keks<br />\n)<br />\n<br />\n)<br />\n<br />\n)<br />\nkris@valiant:~/Source/phpfaq} cat x.xml<br />\n{!DOCTYPE a [<br />\n{!ENTITY y SYSTEM \"y.xml\"}<br />\n]}<br />\n<br />\n<br />\n{a}<br />\n&amp;y;<br />\n{/a}<br />\nkris@valiant:~/Source/phpfaq} cat y.xml<br />\n{c}Keks{/c}<br />\nkris@valiant:~/Source/phpfaq} cat sample.php<br />\n{?php<br />\n$xml = simplexml_load_file(\"x.xml\");<br />\nprint_r($xml);<br />\n?}<br />\nkris@valiant:~/Source/phpfaq} php sample.php<br />\nSimpleXMLElement Object<br />\n(<br />\n[y] =} SimpleXMLElement Object<br />\n(<br />\n[y] =} SimpleXMLElement Object<br />\n(<br />\n[c] =} Keks<br />\n)<br />\n<br />\n)<br />\n<br />\n)<br />\nkris@valiant:~/Source/phpfaq}\n',0,'2004-09-26 16:57:22','NORMAL',1);
