INSERT INTO `blogcomments` (`id`, `comment_posts_id`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_ip`, `comment_date`, `comment_content`, `comment_karma`, `changed`, `comment_type`, `comment_status`) VALUES (1070,1757,'Daniel Veillard\n','','\n','82.67.66.12','2004-08-15 18:31:37','I added this to libxml2 XML parser to be able to recover<br />\nbroken files, but please please, try to get the<br />\nother side fixed rather than setting this by default.<br />\nXML was successful because all parsers actually<br />\nenforced the well formedness rules. You don\'t<br />\nwant XML data flow to be as messy as HTML<br />\ninput, so make your share to avoid the problem !<br />\nIf there is only so much I can ask the PHP<br />\ncommunity, then let it be that you won\'t abuse this<br />\nand keep XML clean.<br />\n<br />\nThanks in advance,<br />\n<br />\nDaniel\n',0,'2004-09-26 16:57:29','NORMAL',1);
