INSERT INTO `blogcomments` (`id`, `comment_posts_id`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_ip`, `comment_date`, `comment_content`, `comment_karma`, `changed`, `comment_type`, `comment_status`) VALUES (1074,1757,'Ian Eure\n','','\n','206.124.138.155','2004-08-19 21:57:50','This \"feature\" defeats one of the main purposes of XML, namely that malformed documents are rejected. Malformed XML should *not* be accepted. Period. When it\'s malformed, it\'s not XML, it\'s just more tag soup.<br />\n<br />\nThis way lies madness. We\'ll get into the same situation as with HTML. Please revert this.\n',0,'2004-09-26 16:57:29','NORMAL',1);
