INSERT INTO `blogcomments` (`id`, `comment_posts_id`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_ip`, `comment_date`, `comment_content`, `comment_karma`, `changed`, `comment_type`, `comment_status`) VALUES (1094,1768,'Garvin\n','','\n','217.76.101.162','2004-08-31 14:08:31','You might want to take notice that \'_\' acts as a word combining character for search engines, whereas \"-\" or \"+\" would seperate them.<br />\n<br />\nMore precise: A URL \"my_nifty_word\" will get a low score when searching for \"nifty\", but \"my-nifty-word\" would get a higher score for that part, because google and the likes treat the last one as separated words, and the first URL as substrings.<br />\n<br />\n(http://www.webmasterworld.com/forum3/23371.htm, http://www.webmasterworld.com/forum3/24738.htm, http://www.webmasterworld.com/forum3/2732.htm)\n',0,'2004-09-26 16:57:32','NORMAL',1);
