INSERT INTO `blogposts` (`id`, `post_author`, `post_date`, `post_content`, `post_title`, `post_category`, `post_karma`, `post_uri`, `changed`, `post_comment_mode`, `post_status`) VALUES (1749,'3','2004-08-02 11:57:10','My two proposals for the <a href=\"http://www.phpconference.org/\">PHP Conference</a> this fall in Frankfurt were accepted.<br />\n<br />\n<i>\"XML on Crack - The hidden beauties of XML in PHP.\"</i> is about all new XML things in PHP 5, but less known to most people<br />\n<br />\n<i>\"XML on Speed - How to write fast and scalable PHP/XML code.\"</i> is about how to improve the speed and performance of your XML using PHP scripts.<br />\n<br />\nYou can read more about the details at the <a href=\"http://www.phpconference.org/2004/session-xml_en.php\">conference website</a><br />\n<br />\nHope to see you there (or at least reading my slides after the conf ,) )\n','PHP Conf: XML on Speed/Crack\n',0,0,'php_conf_xml_on_speed_crack','2004-08-02 09:57:10',1,1);
INSERT INTO `blogposts` (`id`, `post_author`, `post_date`, `post_content`, `post_title`, `post_category`, `post_karma`, `post_uri`, `changed`, `post_comment_mode`, `post_status`) VALUES (1750,'3','2004-08-06 08:31:46','Our latest client project - <a href=\"http://www.berghilfe.ch\">http://www.berghilfe.ch</a> - is online since yesterday. It\'s the website of the Swiss charity organisation \"Schweizer Berghilfe\", which helps mountain farmers here in the Alps.<br />\n<br />\nThe new website is built with our new PHP 5 based Bitflux CMS, has clean XHTML code with proper CSS all over the place and has the usual features like site-search, multi-lingual content, online \"shopping\" and more.<br />\n<br />\nThe project was a joint effort by <a href=\"http://www.oos.com\">OOS</a> (coordination), <a href=\"http://monorom.to\">monorom.to</a> (XHTML templating) and <a href=\"http://bitflux.ch\">Bitflux</a> (CMS). The design and content was done by <a href=\"http://festland.ch\">Festland</a>. And I really like to thank everyone involved in this project and for\ntheir efforts. It was a very enjoyable project.<br />\n<br />\nIf you want to know more about our new CMS, go to our <a href=\"http://wiki.bitflux.org\">Wiki</a> or subscribe to our <a href=\"http://wiki.bitflux.org/Support#Mailing_Lists\">mailinglists</a>.\n','www.berghilfe.ch\n',0,0,'www_berghilfe_ch','2004-08-06 06:31:46',1,1);
