INSERT INTO `blogposts` (`id`, `post_author`, `post_date`, `post_content`, `post_title`, `post_category`, `post_karma`, `post_uri`, `changed`, `post_comment_mode`, `post_status`) VALUES (1762,'3','2004-08-25 08:26:26','The <a href=\"http://www.phpug.ch\">PHP Usergroup Switzerland</a> invites to its next usermeeting, bigger than ever and with a lot of people of the PHP scene.<br />\n<br />\nFirst:<br />\n<i>Battle of the Books</i><br />\nTwo brand new (german) books about PHP 5 face each other in public:<br />\n<ul>\n <li>\n  <a href=\"http://www.mitp.de/vmi/mitp/detail/pWert/1482\">PHP de Luxe - PHP5 Edition</a><br />\n  with <a href=\"http://gassert.ch\">Hannes Gassert</a> and myself\n </li>\n <li>\n  <a href=\"http://www.dpunkt.de/buch/3-89864-300-X.html\">PHP f&#252;r Fortgeschrittene</a><br />\n  with <a href=\"http://www.sitepoint.com/blog-view.php?blogid=9\">Harry Fuecks</a> und <a href=\"http://www.zzoss.com\">Sandro Zic</a>\n </li>\n</ul><br />\nSecond:<br />\n<i>Roaring Regexps</i><br />\nA tutorial about Regular Expressions by <a href=\"http://www.derickrethans.nl/\">Derick Rethans</a>, core developer of PHP, xdebug and much more .<br />\n<br />\nThird:<br />\n<i>Socializing</i><br />\nThe usual stuff. Meet and talk to other PHP developers. Due to the fact, that <a href=\"http://www.oscom.org/\">OSCOM</a> starts the next day, even more international PHP developers will be present at this meeting.<br />\n<br />\nPlace: <a href=\"http://www.etoy.com/relay/\">etoy.TANK-PLANT4</a>, MFO-Park, Oerlikon, Zurich Nord.<br />\nDate: Tuesday, 28 September 2004, 18:00<br />\n<br />\nEveryone\'s invited, from beginners to experts, and members as well as non-members.<br />\n<br />\nMore info (in german) at the <a href=\"http://phpug.ch/blog/?p=65\">phpug blog</a> and please <a href=\"http://www.phpug.ch/phorum/read.php?f=3&amp;i=432&amp;t=432\">leave a comment in the forum</a>, if you\'d like to come.\n','phpug.ch meeting 2/2004: the battle of the books\n',0,0,'phpug_ch_meeting_2_2004_the_battle_of_the_books','2004-08-25 06:26:26',1,1);
